    // input.input.value = "2016-09-24T10:33";
    // input.input.value = "2016-09-24T10:50:00.000Z";
    // input.input.value = "2016-09-24";
    // input.input.value = "2016-09-24T15:00";
    input.input.type = "datetime-local";

    // console.log("preDate: " + preDate);

